INSERT INTO `game` (`id`, `name`, `category`, `time_frame`, `group_size`, `image_name`, `facility_level`, `description`) VALUES
(1, 'Dotmocracy', 'Акција', '5-30 минути', '3-40', 'Dotmocracy.png', 'Почетно', 'Едноставен метод за групна приоритизација или донесување одлуки. Ова не е активност сама по себе, туку метод што треба да се користи во процесите каде што се јавува приоритет или одлучувањето е цел. Со овој групен методот за брзо се гледа кои опции се најпопуларни или релевантни. Опциите или идеите се напишани на стикери или залепени на ѕид за цела група да ги види. Секое лице гласа за можностите за кои смета дека се најиздржани, а таа информација се користи за да се донесе  одлука.'),
(2, 'Project Mid-way Evaluation ', 'Акција', '30-60 минути', '2-10', 'Mid-way.png', 'Средно', 'Овој метод е корисен за оценување на проект во тек, за да се види дали треба да се направат корекции за тимот да работи поефективно заедно. Обезбедува рамка за дискусија. Учесниците се фокусираат на работите што помагаат или го попречуваат тимскиот процес и создаваат чекори за подобрување.'),
(3, 'Тhe 5 Whys ', 'Иновација', '30-60 минути', '2-10', 'Тhe5Whys.png', 'Почетно', 'Овој едноставен и моќен метод е корисен за откривање на јадрото на проблемот или предизвикот. Како што сугерира насловот, групата дефинира проблеми, а потоа го поставува прашањето \"зошто\" пет пати, често користејќи го даденото објаснување како почетна точка за креативно решавање на проблеми.'),
(4, 'Future Trends ', 'Иновација', '60-120 минути', '2-40', 'FutureTrends.png', 'Средно', 'Оваа алатка им помага на малите и големите групи да ги идентификуваат клучните трансформативни трендови во текот на следните три години, да ги истражат нивните последици и да почнат да гледаат како можат да се исполнат за да создадат можности за бизнис. До крајот на работилницата, учесниците креираат листа на најрелевантни трендови; и разработка на три најважни трендови, вклучувајќи идеи околу тоа како нивната организација би можела да дејствува за да ги исполни.'),
(5, 'Story Building ', 'Иновации', '30-60 минути', '2-40', 'StoryBuilding.png', 'Почетно', 'Кратка вежба за да се донесе \"градењето приказна\" во живот: клучен концепт во мрежните дигитални комуникации'),
(6, 'Тake a Stand ', 'Иновации', '60-120 минути', '2-40', 'ТakeaStand.png', 'Средно', 'Ова е практичен, динамичен и разноврсен метод за групи, да истражуваат идеи и прашања заедно. Нешто како физички прашалник; учесниците одговараат на прашања со одење околу просторот и ставајќи се на замислена линија. Ова обезбедува почетна точка за размислување и дискусија, и го зајакнува тимскиот дух.'),
(7, 'IDOARRT Meeting Design ', 'Акција', '5-30 минути', '3-40', 'IDOARRTMeetingDesign.png', 'Почетно', 'IDOARRT е едноставна алатка која ќе ви помогне да водите ефикасен состанок или групен процес со поставување јасна цел, структура и цели на самиот почеток. Таа има за цел да им овозможи на сите учесници да го разберат секој аспект од состанокот или процесот, со што се создава сигурност со заедничка основа, како за почеток. Акронимот се залага за намера, посакуван исход, агенда, правила, улоги и одговорности и време.'),
(8, '3 Action Steps ', 'Акција', '120-240 минути', '2-10', '3ActionSteps.png', 'Средно', 'Ова е мала стратешка сесија за планирање која им помага на групи и поединци да преземат акција кон посакуваните промени. Често се користи на крајот од работилницата или програмата. Групата дискутира и се согласува со визија, а потоа создава некои чекори за дејствување кои ќе ги водат кон таа визија. Обемот на предизвикот исто така е дефиниран, преку дискусија за корисни и штетни фактори кои влијаат врз групата.'),
(9, 'Letter to Myself ', 'Акција', '5-30 минути', '2-40+', 'LettertoMyself.png', 'Почетно', 'Целта на оваа вежба е да ги поддржи учесниците во примена на нивните согледувања и учења, со пишување на писмо и испраќање до нивната идна личност. Тие можат да ги дефинираат клучните дејствија кои би сакале да ги преземат во иднина и да ги изразат своите причини зошто е потребно да се случи таа промена'),
(10, 'Аctive Listening ', 'Лидерство', '60-1200 минути', '2-40', 'АctiveListening.png', 'Средно', 'Оваа активност ги поддржува учесниците да размислуваат за едно прашање и да генерираат сопствени решенија користејќи едноставни принципи на активно слушање. Oва е одличен вовед во активното слушање, но исто така може да се користи со групи кои веќе се запознаени со него. Учесниците работат во групи од три и се претвораат во\"субјект\", слушател и набљудувач.'),
(11, 'Feedback: I appreciate ', 'Лидерство', '60-120 минути', '2-40', 'FeedbackIappreciate.png', 'Напредно', 'Редовен и ефективен фидбек е еден од најважните состојки во градењето конструктивни односи и просперитетни тимови. Отвореноста создава доверба и довербата создава поголема отвореност. Вежбите за фидбек имаат за цел да ги поддржат групите за да се изгради доверба и отвореност и за поединците да стекнат самосвест и увид. Вежбите за фидбек треба секогаш да се изведуваат со внимателност и висока свесност за групната динамика. Ова е добра рана повратна вежба кога членовите на групата веќе имаат развиено некоја удобност и сè уште се запознаваат меѓусебно. Се фокусира на споделување на благодарност и љубопитност едни за други.'),
(12, 'Explore your values ', 'Лидерство', '60-120 минути', '2-40', 'ExploreYourValues.png', 'Средно', 'Едноставен метод за групна приоритизација или донесување одлуки. Ова не е активност сама по себе, туку метод што треба да се користи во процесите каде што се јавува приоритет или одлучувањето е цел. Со овој групен методот за брзо се гледа кои опции се најпопуларни или релевантни. Опциите или идеите се напишани на стикери или залепени на ѕид за цела група да ги види. Секое лице гласа за можностите за кои смета дека се најиздржани, а таа информација се користи за да се донесе  одлука.'),
(13, 'Reflection Individual', 'Лидерство', '30-60 минути', '2-40', 'ReflectionIndividual.png', 'Средно', 'Цел: Индивидуалната рефлексија помага да ги одвоиме комплексните искуства, така што успесите на искуството може да се повторат или подобрат, а неуспесите обезбедуваат можности за учење и раст. Форматот е флексибилен, ве води низ клучните фази на процесот на размислување и завршува со клучните точки за акција.'),
(14, 'Back-turned Feedback ', 'Лидерство', '60-120 минути', '2-40', 'Back-turnedFeedback.png', 'Напредно', 'Вежбите за фидбек имаат за цел да ги поддржат групите за да се изгради доверба и отвореност, за поединците да стекнат самосвест и увид. Вежбите за повратни информации секогаш треба да се водат со внимателности високата свесност за групната динамика.'),
(15, 'Conflict Responses ', 'Тим', '60-120 минути', '2-40', 'ConflictResponses.png', 'Почетно', 'Работилница за тим кој ќе размислува за минатите конфликти и ги користи за да генерира насоки за ефикасно ракување со конфликти. Користете ja за да отворите дискусија.'),
(16, 'Myers-Briggs Team Reflection ', 'Тим', '60-120 минути', '2-10', 'Myers-BriggsTeamReflection.png', 'Почетно', 'description-фали'),
(17, 'Personal Presentations ', 'Тим', '60-240 минути', '2-40', 'PersonalPresentations.png', 'Средно', 'Едноставна вежба во која секој учесник подготвува лична презентација за себе и споделува  неколку важни искуства, настани, луѓе или приказни кои придонеле за негово или нејзино како поединец. Целта на личните презентации е да се поддржи секој учесник со цел да се запознаат едни со други и да градат доверба и отвореност во групата.'),
(18, 'Circles of influence', 'Акција', '30-120 минути', '2-40+', 'Circlesofinfluence.png', 'Почетно', 'Работилница за преглед на тимските приоритети и правење избор на што треба да се фокусирате индивидуално, како и колективно. Работилницата ги предизвикува членовите да размислуваат каде можат да имаат најголемо влијание и импакт. Користете ја оваа работилница за да ги усовршите приоритетите.');

